<?php class Mydb extends CI_Controller{	public function __construct(){		parent::__construct();		$this->load->model('test');$this->load->helper('url');	}public function show_some_tests(){                 				$data['test'] = $this->test->get_first_ten() ;  //MVC model				$this->load->view('db-test',$data);		}						public function add_form(){				$this->load->view('add-form');				}				public function add(){			$data['testID'] = $this->test->insert($this->input->post('name'));  //not recommended to use post here it might be an attack					$this->load->view("show-test", $data);		}}?>