<?phpclass Test extends CI_Model{		public function get_first_ten(){		 $test = array();				  $i = 0;			$query = $this->db->query("select * from test limit 10");				foreach($query->result() as $row){				$test[$i]['id'] = $row->id;				$test[$i]['name'] = $row->name;								$i++;		}		return $test;		}						public function insert($name){		   //$this->db->query("insert into test set name='$name'");		   $this->db->insert('test', array('name' => $name));		   		   return $this->db->insert_id();				}		}?>